import React, { useState, useEffect } from 'react';
import { getAuth } from 'firebase/auth';

function AdminDashboard() {
  const auth = getAuth();
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    email: '',
    role: 'agent',
    assignedClients: []
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    // În MVP, vom folosi date mock pentru testare
    setUsers([
      {
        id: '1',
        email: 'admin@example.com',
        role: 'admin',
        assignedClients: ['1', '2', '3']
      },
      {
        id: '2',
        email: 'agent@example.com',
        role: 'agent',
        assignedClients: ['1']
      },
      {
        id: '3',
        email: 'nurse@example.com',
        role: 'nurse',
        assignedClients: ['1', '2']
      }
    ]);
  }, []);

  const handleAddUser = (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      // Validare de bază
      if (!newUser.email) {
        throw new Error('Email is required');
      }

      // În MVP, vom simula adăugarea unui utilizator
      console.log('Adding new user:', newUser);

      // Simulăm un delay pentru a arăta feedback-ul
      setTimeout(() => {
        setUsers([...users, { id: Date.now().toString(), ...newUser }]);
        setSuccess('User added successfully!');
        setNewUser({
          email: '',
          role: 'agent',
          assignedClients: []
        });
      }, 1000);

    } catch (error) {
      setError(error.message);
    }
  };

  const handleDeleteUser = (userId) => {
    // În MVP, vom simula ștergerea unui utilizator
    setUsers(users.filter(user => user.id !== userId));
    setSuccess('User deleted successfully!');
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h2 className="text-lg font-medium text-gray-900">Admin Dashboard</h2>

            {/* Add User Form */}
            <div className="mt-6">
              <h3 className="text-md font-medium text-gray-900">Add New User</h3>
              <form onSubmit={handleAddUser} className="mt-4 space-y-4">
                {error && (
                  <div className="bg-red-50 p-4 rounded-md">
                    <div className="text-sm text-red-700">{error}</div>
                  </div>
                )}
                {success && (
                  <div className="bg-green-50 p-4 rounded-md">
                    <div className="text-sm text-green-700">{success}</div>
                  </div>
                )}

                <div>
                  <label className="block text-sm font-medium text-gray-700">Email</label>
                  <input
                    type="email"
                    value={newUser.email}
                    onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                    className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Role</label>
                  <select
                    value={newUser.role}
                    onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
                    className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                  >
                    <option value="agent">Call Center Agent</option>
                    <option value="nurse">Nurse</option>
                    <option value="admin">Admin</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Assigned Clients</label>
                  <div className="mt-2 space-y-2">
                    <label className="inline-flex items-center">
                      <input
                        type="checkbox"
                        checked={newUser.assignedClients.includes('1')}
                        onChange={(e) => {
                          const clients = e.target.checked
                            ? [...newUser.assignedClients, '1']
                            : newUser.assignedClients.filter(id => id !== '1');
                          setNewUser({ ...newUser, assignedClients: clients });
                        }}
                        className="form-checkbox h-4 w-4 text-indigo-600"
                      />
                      <span className="ml-2 text-gray-700">Hospice Care A</span>
                    </label>
                    <label className="inline-flex items-center ml-4">
                      <input
                        type="checkbox"
                        checked={newUser.assignedClients.includes('2')}
                        onChange={(e) => {
                          const clients = e.target.checked
                            ? [...newUser.assignedClients, '2']
                            : newUser.assignedClients.filter(id => id !== '2');
                          setNewUser({ ...newUser, assignedClients: clients });
                        }}
                        className="form-checkbox h-4 w-4 text-indigo-600"
                      />
                      <span className="ml-2 text-gray-700">Hospice Care B</span>
                    </label>
                    <label className="inline-flex items-center ml-4">
                      <input
                        type="checkbox"
                        checked={newUser.assignedClients.includes('3')}
                        onChange={(e) => {
                          const clients = e.target.checked
                            ? [...newUser.assignedClients, '3']
                            : newUser.assignedClients.filter(id => id !== '3');
                          setNewUser({ ...newUser, assignedClients: clients });
                        }}
                        className="form-checkbox h-4 w-4 text-indigo-600"
                      />
                      <span className="ml-2 text-gray-700">Hospice Care C</span>
                    </label>
                  </div>
                </div>

                <div>
                  <button
                    type="submit"
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                  >
                    Add User
                  </button>
                </div>
              </form>
            </div>

            {/* Users List */}
            <div className="mt-8">
              <h3 className="text-md font-medium text-gray-900">Users</h3>
              <div className="mt-4 flex flex-col">
                <div className="-my-2 -mx-4 overflow-x-auto sm:-mx-6 lg:-mx-8">
                  <div className="inline-block min-w-full py-2 align-middle md:px-6 lg:px-8">
                    <div className="overflow-hidden shadow ring-1 ring-black ring-opacity-5 md:rounded-lg">
                      <table className="min-w-full divide-y divide-gray-300">
                        <thead className="bg-gray-50">
                          <tr>
                            <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Email</th>
                            <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Role</th>
                            <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Assigned Clients</th>
                            <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Actions</th>
                          </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-200 bg-white">
                          {users.map((user) => (
                            <tr key={user.id}>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{user.email}</td>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{user.role}</td>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">
                                {user.assignedClients.map(id => {
                                  const names = {
                                    '1': 'Hospice Care A',
                                    '2': 'Hospice Care B',
                                    '3': 'Hospice Care C'
                                  };
                                  return names[id];
                                }).join(', ')}
                              </td>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">
                                <button
                                  onClick={() => handleDeleteUser(user.id)}
                                  className="text-red-600 hover:text-red-900"
                                >
                                  Delete
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
