import React, { useState, useEffect } from 'react';
import { callService, clientService } from '../../services/localStorage';

function Reports() {
  const [calls, setCalls] = useState([]);
  const [filteredCalls, setFilteredCalls] = useState([]);
  const [clients, setClients] = useState([]);
  const [filters, setFilters] = useState({
    clientId: '',
    category: '',
    dateFrom: '',
    dateTo: '',
    searchTerm: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const loadData = async () => {
      try {
        const [callsData, clientsData] = await Promise.all([
          callService.getCalls(),
          clientService.getClients()
        ]);
        setCalls(callsData);
        setFilteredCalls(callsData);
        setClients(clientsData);
      } catch (error) {
        console.error('Error loading data:', error);
        setError('Error loading data. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    let filtered = [...calls];

    if (filters.clientId) {
      filtered = filtered.filter(call => call.clientId === filters.clientId);
    }

    if (filters.category) {
      filtered = filtered.filter(call => call.categories.includes(filters.category));
    }

    if (filters.dateFrom) {
      filtered = filtered.filter(call => call.timestamp >= filters.dateFrom);
    }

    if (filters.dateTo) {
      filtered = filtered.filter(call => call.timestamp <= filters.dateTo);
    }

    if (filters.searchTerm) {
      const searchLower = filters.searchTerm.toLowerCase();
      filtered = filtered.filter(call => 
        call.patientId.toLowerCase().includes(searchLower) ||
        call.summary.toLowerCase().includes(searchLower)
      );
    }

    setFilteredCalls(filtered);
  }, [filters, calls]);

  const handleExport = () => {
    const headers = ['Client', 'Call Type', 'Patient ID', 'Summary', 'Categories', 'Timestamp'];
    const csvContent = [
      headers.join(','),
      ...filteredCalls.map(call => {
        const client = clients.find(c => c.id === call.clientId);
        return [
          client?.name || 'Unknown Client',
          call.callType,
          call.patientId,
          `"${call.summary}"`,
          call.categories.join(';'),
          call.timestamp
        ].join(',');
      })
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = `call-reports-${new Date().toISOString().slice(0,10)}.csv`;
    link.click();
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-lg text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h2 className="text-lg font-medium text-gray-900">Call Reports</h2>
            
            {/* Filters */}
            <div className="mt-4 grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Client</label>
                <select
                  value={filters.clientId}
                  onChange={(e) => setFilters({ ...filters, clientId: e.target.value })}
                  className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                >
                  <option value="">All Clients</option>
                  {clients.map(client => (
                    <option key={client.id} value={client.id}>
                      {client.name}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Category</label>
                <select
                  value={filters.category}
                  onChange={(e) => setFilters({ ...filters, category: e.target.value })}
                  className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                >
                  <option value="">All Categories</option>
                  <option value="Pain">Pain</option>
                  <option value="Refill">Refill</option>
                  <option value="Constipation">Constipation</option>
                  <option value="Anxiety">Anxiety</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Date From</label>
                <input
                  type="date"
                  value={filters.dateFrom}
                  onChange={(e) => setFilters({ ...filters, dateFrom: e.target.value })}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Date To</label>
                <input
                  type="date"
                  value={filters.dateTo}
                  onChange={(e) => setFilters({ ...filters, dateTo: e.target.value })}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                />
              </div>
            </div>

            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700">Search</label>
              <input
                type="text"
                value={filters.searchTerm}
                onChange={(e) => setFilters({ ...filters, searchTerm: e.target.value })}
                placeholder="Search by patient ID or summary..."
                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              />
            </div>

            <div className="mt-4">
              <button
                onClick={handleExport}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Export to CSV
              </button>
            </div>

            {/* Results Table */}
            <div className="mt-8 flex flex-col">
              <div className="-my-2 -mx-4 overflow-x-auto sm:-mx-6 lg:-mx-8">
                <div className="inline-block min-w-full py-2 align-middle md:px-6 lg:px-8">
                  <div className="overflow-hidden shadow ring-1 ring-black ring-opacity-5 md:rounded-lg">
                    <table className="min-w-full divide-y divide-gray-300">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Client</th>
                          <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Call Type</th>
                          <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Patient ID</th>
                          <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Summary</th>
                          <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Categories</th>
                          <th className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">Timestamp</th>
                        </tr>
                      </thead>
                      <tbody className="divide-y divide-gray-200 bg-white">
                        {filteredCalls.map((call) => {
                          const client = clients.find(c => c.id === call.clientId);
                          return (
                            <tr key={call.id}>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">
                                {client?.name || 'Unknown Client'}
                              </td>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{call.callType}</td>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{call.patientId}</td>
                              <td className="px-3 py-4 text-sm text-gray-500">{call.summary}</td>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">
                                {call.categories.join(', ')}
                              </td>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">
                                {new Date(call.timestamp).toLocaleString()}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Reports;
